import { Button, FormControlLabel } from '@mui/material';
import Box from '@mui/material/Box';
import Checkbox from '@mui/material/Checkbox';
import React, { useState } from 'react';


const Form = () => {
    function RedBar() {
        return (
          <Box
            sx={{ height: 20, backgroundColor: 'transparent' }}
          />
        );
    }

    const [data,setData] = useState({input: {
      email:"",
      name:"",
      password:'',
      cpass:"",
      phone:"",
    },errors: {
      email:"",
      name:"",
      password:'',
      cpass:"",
      phone:"",
    }})
       
    const handleChange = (event) => {
      let input = data.input;
      input[event.target.name] = event.target.value;
      setData({ input });
    }

    const handleSubmit = (event) => {
      event.preventDefault();
    
      // if(validate()){
      //     console.log(data);
      //     data.input["name"] = "";
      //     data.input["email"] = "";
      //     data.input["password"] = "";
      //     data.input["cpass"] = "";
      //     setData({input:""});
    
      //     alert('Demo Form is submited');
      // }
    }
    
    const validate =() =>{
        let input = data.input;
        let errors = data.errors;
        let isValid = true;
    
        if (!input["name"]) {
          isValid = false;
          errors["name"] = "Please enter your name.";
        }
    
        if (!input["email"]) {
          isValid = false;
          errors["email"] = "Please enter your email Address.";
        }
    
        if (typeof input["email"] !== "undefined") {
            
          var pattern = new RegExp(/^(("[\w-\s]+")|([\w-]+(?:\.[\w-]+)*)|("[\w-\s]+")([\w-]+(?:\.[\w-]+)*))(@((?:[\w-]+\.)*\w[\w-]{0,66})\.([a-z]{2,6}(?:\.[a-z]{2})?)$)|(@\[?((25[0-5]\.|2[0-4][0-9]\.|1[0-9]{2}\.|[0-9]{1,2}\.))((25[0-5]|2[0-4][0-9]|1[0-9]{2}|[0-9]{1,2})\.){2}(25[0-5]|2[0-4][0-9]|1[0-9]{2}|[0-9]{1,2})\]?$)/i);
          if (!pattern.test(input["email"])) {
            isValid = false;
            errors["email"] = "Please enter valid email address.";
          }
        }
    
        if (!input["password"]) {
          isValid = false;
          errors["password"] = "Please enter your password.";
        }
    
        if (!input["cpass"]) {
          isValid = false;
          errors["confirm_password"] = "Please enter your confirm password.";
        }
    
        if (typeof input["password"] !== "undefined" && typeof input["cpass"] !== "undefined") {
            
          if (input["password"] !== input["cpass"]) {
            isValid = false;
            errors["password"] = "Passwords don't match.";
          }
        } 
    
        setData({
          errors: errors
        });
    
        return isValid;
    }    

  return (
    <div className="form">
    <div className="form__details">
        <h2>Create an account</h2>
      <form onSubmit= {handleSubmit}>
        <RedBar/>
        <div className="form--text">
        <label htmlFor="email">Your email Address</label>
        <input type="text" id="email" name="email" placeholder="Enter your Email Address" value={data.input.email} onChange={handleChange} pattern="[a-zA-Z0-9._%+-]+@[a-z0-9.-]+\.[a-zA-Z]{2,4}" required/>
        <div className="text-danger">{data.errors.email}</div>
        </div>
        <RedBar/>
        <div className="form--text">
        <label htmlFor="password">Your password</label>
        <input type="password" id="password" name="password" value={data.input.password} onChange={handleChange} placeholder="Enter your password" required/>
        <div className="text-danger">{data.errors.password}</div>
        </div>    
        <RedBar/><div className="form--text">
        <label htmlFor="cpass">Confirm your password</label>
        <input type="password" id="cpass" name="cpass" value={data.input.cpass} onChange={handleChange} placeholder="Enter your password" required/>
        <div className="text-danger">{data.errors.cpass}</div>
        </div>
        <RedBar/>    
        <div className="form--text">
        <label htmlFor="name">Your name</label>
        <input type="text" id="name" name="name" value={data.input.name} onChange={handleChange} placeholder="Enter your Name" required/>
        </div>
        <RedBar/>
        <div className="form--text">
        <label  htmlFor="phone">Your phone number</label><br/>
        <input type="text" id="phone" name="phone" value={data.input.phone} onChange={handleChange} placeholder="Enter your Phone Number" pattern="[6-9]{1}[0-9]{9}" required/>
        </div>
        <RedBar/>
        <FormControlLabel
          value="end" labelPlacement="end"
          control={<Checkbox defaultChecked />} label="I read and agree Terms and Conditions" size="small" 
          sx={{ '& .MuiTypography-root': { fontWeight: "bold",color: 'black'},}}
        />
        <RedBar/>
        <Button type='submit' variant='contained' color='primary'>Create Account</Button>
      </form>
    </div>
    </div>
  )
}

export default Form;
