import React, { Component }  from 'react';
import * as d3 from "d3";

//   var svg = d3.select('#chartholder').selectAll('div')
// 			.data(data)
// 			.enter()
// 			.append('div')
// 			.attr('class', 'bar')
// 			.style('height', function (d) {
// 				return d * 400 + 'px';
// 			});
class Dashboard extends Component {
  componentDidMount() {
    this.drawChart();
  }
  
  drawChart() {
    const data1 = d3.range(10).map(function() {
      return Math.random();
    });
    const data2 = [13.27, 50.5, 30.1, 12.15, 10.3, 67.21, 44.25, 11.94, 8.04, 31.00];
    const data = data1.concat(data2);
    console.log(data);
    
    const svg = d3.select("body")
    .append("svg")
    .attr("width", 400)
    .attr("height", 600)
    .style("margin-left", 100);
                  
    svg.selectAll("rect")
      .data(data)
      .enter()
      .append("rect")
      .attr("x", (d, i) => i * 70)
      .attr("y", (d, i) => 600 - 10 * d)
      .attr("width", 65)
      .attr("height", (d, i) => d * 10)
      .attr("fill", "green")
  }
        
  render(){
    return <div id={"#" + this.props.id}></div>
  }
}    

export default Dashboard;
