import React, { Component }  from 'react';
import * as d3 from "d3";

//   var svg = d3.select('#chartholder').selectAll('div')
// 			.data(data)
// 			.enter()
// 			.append('div')
// 			.attr('class', 'bar')
// 			.style('height', function (d) {
// 				return d * 400 + 'px';
// 			});
class Dashboard extends Component {
  componentDidMount() {
    this.drawChart();
  }
  
  drawChart() {
    const data = d3.range(10).map(function() {
      return Math.random();
    });
    console.log(data);
    
    const svg = d3.select("body")
    .append("svg")
    .attr("width", 900)
    .attr("height", 600)
    .style("margin-left", 100);
                  
    svg.selectAll("text")
      .data(data)
      .enter()
      .append("text")
      .text((d) => d)
      .attr("x", (d, i) => i * 70)
      .attr("y", (d, i) => 600 - (10 * d) - 3)
      .attr("width", 65)
      .attr("height", (d, i) => d * 10)
      .attr("fill", "green")
  }
        
  render(){
    return <div id={"#" + this.props.id}></div>
  }
}    

export default Dashboard;
