class Dashboard extends Component {
  componentDidMount() {
    this.drawChart();
  }

  
    
  drawChart() {
    const data = d3.range(10).map(function() {
      return 50+Math.random();
    });
    console.log(data);
    var width = 750;
    var height = 600;
    
    const svg = d3.select("body")
    .append("svg")
    .attr("width", width)
    .attr("height", height)
    .style("margin-left", 100);
                  
    svg.selectAll("rect")
      .data(data)
      .enter()
      .append("rect")
      .attr("x", (d, i) => i * 70)
      .attr("y", (d, i) => height - 10 * d)
      .attr("width", 65)
      .attr("height", (d, i) => d * 10)
      .attr("fill", "green")
  }
        
  render(){
    return <div id={"#" + this.props.id}></div>
  }
}    

export default Dashboard;
